function process(s) {
    //console.log(s);
    var pattern = /(?:.*?)发信人:(.+?) \((.+?)\), 信区:(.+?)<br\/>(?:.*)标(?:.*?)题:(.+?)<br\/>(?:.*?)发信站:(.+?)\((.+?)\)(?:.*?)站内(.+?)※ 来源:(.+?)\[FROM: (.+?)\]/mg;
    var names = new Array("user", "nick", "board", "title", "mailsite", "time", "content", "source", "ip");
    var obj = {};
    var r = pattern.exec(s);
    if (r) {
        for (var i = 1; i < r.length; i++) {
            var ret = r[i].replace(/<br\/>/g, "\n").replace(/<(?:.|\s)*?>/g, "");
            obj[names[i-1]] = ret.trim();
        }
    }

    var img_pattern = /单击此查看原图(?:.*?)src="(.+?)"/mg;
    r = img_pattern.exec(s);
    var imgs = [];
    while (r) {
        imgs.push(r[1]);
        r = img_pattern.exec(s);
    }
    if (imgs.length > 0) {
        obj.imgs = imgs;
    }

    console.log(JSON.stringify(obj));
    return obj;
}

article.post_processor:
function copy_default(s, d) {
    d.from_url_ = s.from_url_;
    d.crawl_time_ = s.crawl_time_;
    d.from_parser_name_ = s.from_parser_name_;
}
function process(r) {
    console.log(JSON.stringify(r));
    var results = [];
    for (var i = 0; i < r.length; i++) {
        if ('posts' in r[i]) {
            var article = r[i].from_url_;
            var p = /([^\/]+?\/\d+)/g;
            var m = p.exec(article);
            if (m) {
                article = m[1];
            }

            for (var j = 0; j < r[i].posts.length; j++) {
                var item = r[i].posts[j].content;
                item.floor = r[i].posts[j].floor;
                if (item.floor == '楼主') item.floor = "0";
                copy_default(r[i], item);
                item.es_type = "posts";
                item.id = article + "#" + item.floor;
                results.push(item);
            }
        }
    }
    return results;
}
